import React from "react";
import { render } from "react-dom";
import Container from "./Container";
import "whatwg-fetch";

export const pack = components => (
    state,
    modules,
    elementId,
    wrapper,
    recorder
) => {
    render(
        <Container
            components={components}
            state={state}
            modules={modules}
            wrapper={wrapper}
            recorder={recorder}
        />,
        document.getElementById(elementId)
    );
};
